import numpy as np
import random
classes=[1,2,3]
a=random.randint(1,3)
import pickle as p

def creat_data1(d):
    a=random.uniform(0,1)
    if d==3:
        if a<=0.2:
            return 1
        elif 0.2<a<0.4:
            return  2
        else:
            return 3
    elif d==2:
        if a<=0.2:
            return 1
        elif 0.2<a<0.8:
            return  2
        else:
            return 3
    elif d==1:
        if a<=0.6:
            return 1
        elif 0.6<a<0.8:
            return  2
        else:
            return 3

def creat_data(D,shux):
    '''
    自动创建离散数据表
    :param D:
    :param duix:
    :param shux:
    :return:
    '''
    data=np.zeros((len(D),shux+1))
    for i in range(len(D)):
        for j in range(shux):
            s=creat_data1(D[i])
            data[i,j]=s
        data[i,-1]=D[i]
    p.dump(data,open("data.p",'wb'))
    return data

